contracts/Staking.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

/**
 * @title VEG21 Staking Contract
 * @dev Simple staking contract for VEG21 tokens on Astar Network
 * @notice Users can stake and unstake VEG21 tokens to earn rewards
 */
contract VEG21Staking {
    // State variables
    mapping(address => uint256) private stakes;
    mapping(address => uint256) private stakingTimestamps;

    uint256 public totalStaked;
    uint256 public rewardRate = 100; // 1% daily reward (100 basis points)
    uint256 public constant SECONDS_PER_DAY = 86400;

    // Events
    event Staked(address indexed user, uint256 amount, uint256 timestamp);
    event Unstaked(address indexed user, uint256 amount, uint256 timestamp);
    event RewardClaimed(address indexed user, uint256 reward, uint256 timestamp);

    // Errors
    error InsufficientBalance();
    error InsufficientStake();
    error ZeroAmount();

    /**
     * @dev Stake tokens to earn rewards
     * @param amount Amount of tokens to stake (in wei)
     */
    function stake(uint256 amount) external payable {
        if (amount == 0) revert ZeroAmount();
        if (msg.value != amount) revert InsufficientBalance();

        // If user already has a stake, claim pending rewards first
        if (stakes[msg.sender] > 0) {
            uint256 pendingReward = calculateReward(msg.sender);
            if (pendingReward > 0) {
                payable(msg.sender).transfer(pendingReward);
                emit RewardClaimed(msg.sender, pendingReward, block.timestamp);
            }
        }

        stakes[msg.sender] += amount;
        stakingTimestamps[msg.sender] = block.timestamp;
        totalStaked += amount;

        emit Staked(msg.sender, amount, block.timestamp);
    }

    /**
     * @dev Unstake tokens and claim rewards
     * @param amount Amount of tokens to unstake (in wei)
     */
    function unstake(uint256 amount) external {
        if (amount == 0) revert ZeroAmount();
        if (stakes[msg.sender] < amount) revert InsufficientStake();

        // Calculate and pay pending rewards
        uint256 pendingReward = calculateReward(msg.sender);

        // Update state
        stakes[msg.sender] -= amount;
        totalStaked -= amount;
        stakingTimestamps[msg.sender] = block.timestamp;

        // Transfer unstaked amount plus rewards
        uint256 totalTransfer = amount + pendingReward;
        if (address(this).balance >= totalTransfer) {
            payable(msg.sender).transfer(totalTransfer);
        } else {
            // Emergency: just transfer what's available
            payable(msg.sender).transfer(address(this).balance);
        }

        emit Unstaked(msg.sender, amount, block.timestamp);
        if (pendingReward > 0) {
            emit RewardClaimed(msg.sender, pendingReward, block.timestamp);
        }
    }

    /**
     * @dev Get user's current staked balance
     * @param user Address of the user
     * @return Current staked amount in wei
     */
    function getStake(address user) external view returns (uint256) {
        return stakes[user];
    }

    /**
     * @dev Get user's staking timestamp
     * @param user Address of the user
     * @return Timestamp when user last staked
     */
    function getStakingTimestamp(address user) external view returns (uint256) {
        return stakingTimestamps[user];
    }

    /**
     * @dev Calculate pending rewards for a user
     * @param user Address of the user
     * @return Pending reward amount in wei
     */
    function calculateReward(address user) public view returns (uint256) {
        if (stakes[user] == 0) return 0;

        uint256 stakingDuration = block.timestamp - stakingTimestamps[user];
        uint256 dailyReward = (stakes[user] * rewardRate) / 10000;

        return (dailyReward * stakingDuration) / SECONDS_PER_DAY;
    }

    /**
     * @dev Get pending rewards for caller
     * @return Pending reward amount in wei
     */
    function getPendingRewards() external view returns (uint256) {
        return calculateReward(msg.sender);
    }

    /**
     * @dev Claim pending rewards without unstaking
     */
    function claimRewards() external {
        uint256 pendingReward = calculateReward(msg.sender);
        if (pendingReward == 0) revert ZeroAmount();

        stakingTimestamps[msg.sender] = block.timestamp;

        if (address(this).balance >= pendingReward) {
            payable(msg.sender).transfer(pendingReward);
            emit RewardClaimed(msg.sender, pendingReward, block.timestamp);
        }
    }

    /**
     * @dev Get contract's total balance
     * @return Contract balance in wei
     */
    function getContractBalance() external view returns (uint256) {
        return address(this).balance;
    }

    /**
     * @dev Get contract info
     * @return totalStaked Total amount staked across all users
     * @return contractBalance Current contract balance
     * @return currentRewardRate Current reward rate in basis points
     */
    function getContractInfo() external view returns (uint256, uint256, uint256) {
        return (totalStaked, address(this).balance, rewardRate);
    }

    // Allow contract to receive ETH
    receive() external payable {
        // Contract can receive ETH for rewards pool
    }

    fallback() external payable {
        // Contract can receive ETH for rewards pool
    }
}

contracts/VEG21Donations.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "./VEG21Token.sol";

/**
 * @title VEG21 Donations Contract
 * @notice Manages charitable donations using VEG21 tokens
 * @dev Tracks donations to vegan charities and burns donated tokens
 * 
 * Features:
 * - Donate VEG21 tokens to registered charities
 * - Tokens are burned upon donation (deflationary)
 * - Track total donations per charity
 * - Track individual donor contributions
 * - Owner can register/unregister charities
 * 
 * Impact Tracking:
 * - Total tokens donated (burned)
 * - Per-charity donation totals
 * - Per-donor contribution history
 * - Leaderboard integration for top donors
 */
contract VEG21Donations is Ownable, ReentrancyGuard {
    VEG21Token public immutable veg21Token;

    // Charity struct
    struct Charity {
        string name;
        string description;
        address wallet;
        bool isActive;
        uint256 totalDonations;
    }

    // Mappings
    mapping(uint256 => Charity) public charities;
    mapping(address => mapping(uint256 => uint256)) public userDonations; // user => charityId => amount
    mapping(address => uint256) public totalUserDonations;

    uint256 public charityCount;
    uint256 public totalDonationsAllTime;

    // Events
    event CharityRegistered(uint256 indexed charityId, string name, address wallet);
    event CharityDeactivated(uint256 indexed charityId);
    event DonationMade(
        address indexed donor,
        uint256 indexed charityId,
        uint256 amount,
        uint256 timestamp
    );

    /**
     * @dev Constructor
     * @param _veg21Token Address of the VEG21 token contract
     * @param initialOwner Address of the initial owner
     */
    constructor(address _veg21Token, address initialOwner) Ownable(initialOwner) {
        require(_veg21Token != address(0), "VEG21Donations: token address cannot be zero");
        veg21Token = VEG21Token(_veg21Token);
    }

    /**
     * @dev Registers a new charity
     * @param name Name of the charity
     * @param description Brief description
     * @param wallet Charity's wallet address for transparency
     */
    function registerCharity(
        string memory name,
        string memory description,
        address wallet
    ) external onlyOwner {
        require(bytes(name).length > 0, "VEG21Donations: name cannot be empty");
        require(wallet != address(0), "VEG21Donations: wallet cannot be zero address");

        uint256 charityId = charityCount++;

        charities[charityId] = Charity({
            name: name,
            description: description,
            wallet: wallet,
            isActive: true,
            totalDonations: 0
        });

        emit CharityRegistered(charityId, name, wallet);
    }

    /**
     * @dev Deactivates a charity (prevents new donations)
     * @param charityId ID of the charity to deactivate
     */
    function deactivateCharity(uint256 charityId) external onlyOwner {
        require(charityId < charityCount, "VEG21Donations: invalid charity ID");
        require(charities[charityId].isActive, "VEG21Donations: charity already inactive");

        charities[charityId].isActive = false;
        emit CharityDeactivated(charityId);
    }

    /**
     * @dev Makes a donation to a charity
     * @param charityId ID of the charity to donate to
     * @param amount Amount of VEG21 tokens to donate (in wei)
     * 
     * Note: Donated tokens are burned (deflationary)
     */
    function donate(uint256 charityId, uint256 amount) external nonReentrant {
        require(amount > 0, "VEG21Donations: donation amount must be positive");
        require(charityId < charityCount, "VEG21Donations: invalid charity ID");
        require(charities[charityId].isActive, "VEG21Donations: charity is not active");

        // Transfer tokens from donor to contract
        require(
            veg21Token.transferFrom(msg.sender, address(this), amount),
            "VEG21Donations: transfer failed"
        );

        // Burn the donated tokens
        veg21Token.burnFrom(address(this), amount, "charity_donation");

        // Update donation tracking
        charities[charityId].totalDonations += amount;
        userDonations[msg.sender][charityId] += amount;
        totalUserDonations[msg.sender] += amount;
        totalDonationsAllTime += amount;

        emit DonationMade(msg.sender, charityId, amount, block.timestamp);
    }

    /**
     * @dev Returns charity information
     * @param charityId ID of the charity
     */
    function getCharity(uint256 charityId) 
        external 
        view 
        returns (
            string memory name,
            string memory description,
            address wallet,
            bool isActive,
            uint256 totalDonations
        ) 
    {
        require(charityId < charityCount, "VEG21Donations: invalid charity ID");
        Charity storage charity = charities[charityId];

        return (
            charity.name,
            charity.description,
            charity.wallet,
            charity.isActive,
            charity.totalDonations
        );
    }

    /**
     * @dev Returns user's total donations to a specific charity
     * @param user Address of the user
     * @param charityId ID of the charity
     */
    function getUserDonationToCharity(address user, uint256 charityId) 
        external 
        view 
        returns (uint256) 
    {
        return userDonations[user][charityId];
    }

    /**
     * @dev Returns list of all active charities
     * @return activeCharityIds Array of active charity IDs
     */
    function getActiveCharities() external view returns (uint256[] memory) {
        uint256 activeCount = 0;

        // Count active charities
        for (uint256 i = 0; i < charityCount; i++) {
            if (charities[i].isActive) {
                activeCount++;
            }
        }

        // Build array of active charity IDs
        uint256[] memory activeCharityIds = new uint256[](activeCount);
        uint256 index = 0;

        for (uint256 i = 0; i < charityCount; i++) {
            if (charities[i].isActive) {
                activeCharityIds[index++] = i;
            }
        }

        return activeCharityIds;
    }
}

contracts/VEG21Rewards.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "./VEG21Token.sol";

/**
 * @title VEG21 Rewards Contract
 * @notice Manages challenge completion rewards for VEG21 users
 * @dev Allows authorized verifiers to distribute rewards for completed challenges
 * 
 * Features:
 * - Reward users for completing 21-day vegan challenges
 * - Multiple reward tiers based on challenge difficulty
 * - Daily check-in rewards
 * - Challenge completion bonuses
 * - Owner-controlled verifier management
 * - Prevents duplicate reward claims
 * 
 * Reward Types:
 * - Daily Check-In: +5 VEG21 per day
 * - 21-Day Completion: +50 VEG21 bonus
 * - Challenge Types: Meat-Free, Vegan Meals, Full Vegan, Zero Waste
 */
contract VEG21Rewards is Ownable, ReentrancyGuard {
    VEG21Token public immutable veg21Token;

    // Reward amounts (in whole tokens, converted to wei when minting)
    uint256 public dailyCheckInReward = 5 * 10**18;      // 5 VEG21
    uint256 public challengeCompletionBonus = 50 * 10**18; // 50 VEG21

    // Challenge types
    enum ChallengeType {
        MeatFree,
        VeganMeals,
        FullVegan,
        ZeroWaste
    }

    // Challenge tracking
    struct Challenge {
        address user;
        ChallengeType challengeType;
        uint256 startDate;
        uint256 checkInsCompleted;
        bool isComplete;
        bool bonusClaimed;
    }

    // Mappings
    mapping(address => Challenge) public userChallenges;
    mapping(address => uint256) public totalRewardsEarned;
    mapping(address => bool) public isVerifier;

    // Stats
    uint256 public totalChallengesCompleted;
    uint256 public totalRewardsDistributed;

    // Events
    event ChallengeStarted(address indexed user, ChallengeType challengeType, uint256 startDate);
    event CheckInRewarded(address indexed user, uint256 day, uint256 reward);
    event ChallengeCompleted(address indexed user, uint256 completionBonus);
    event VerifierAdded(address indexed verifier);
    event VerifierRemoved(address indexed verifier);
    event RewardAmountsUpdated(uint256 dailyReward, uint256 completionBonus);

    /**
     * @dev Constructor
     * @param _veg21Token Address of the VEG21 token contract
     * @param initialOwner Address of the initial owner
     */
    constructor(address _veg21Token, address initialOwner) Ownable(initialOwner) {
        require(_veg21Token != address(0), "VEG21Rewards: token address cannot be zero");
        veg21Token = VEG21Token(_veg21Token);

        // Owner is automatically a verifier
        isVerifier[initialOwner] = true;
    }

    modifier onlyVerifier() {
        require(isVerifier[msg.sender], "VEG21Rewards: caller is not a verifier");
        _;
    }

    /**
     * @dev Starts a new challenge for a user
     * @param user Address of the user
     * @param challengeType Type of challenge (0-3)
     */
    function startChallenge(address user, ChallengeType challengeType) 
        external 
        onlyVerifier 
    {
        require(user != address(0), "VEG21Rewards: user cannot be zero address");
        require(
            userChallenges[user].startDate == 0 || userChallenges[user].isComplete,
            "VEG21Rewards: user already has an active challenge"
        );

        userChallenges[user] = Challenge({
            user: user,
            challengeType: challengeType,
            startDate: block.timestamp,
            checkInsCompleted: 0,
            isComplete: false,
            bonusClaimed: false
        });

        emit ChallengeStarted(user, challengeType, block.timestamp);
    }

    /**
     * @dev Records a daily check-in and rewards the user
     * @param user Address of the user
     */
    function recordCheckIn(address user) external onlyVerifier nonReentrant {
        Challenge storage challenge = userChallenges[user];

        require(challenge.startDate > 0, "VEG21Rewards: no active challenge");
        require(!challenge.isComplete, "VEG21Rewards: challenge already complete");
        require(challenge.checkInsCompleted < 21, "VEG21Rewards: maximum check-ins reached");

        // Increment check-ins
        challenge.checkInsCompleted++;
        uint256 currentDay = challenge.checkInsCompleted;

        // Mint daily reward
        veg21Token.mint(user, dailyCheckInReward, "daily_checkin");
        totalRewardsEarned[user] += dailyCheckInReward;
        totalRewardsDistributed += dailyCheckInReward;

        emit CheckInRewarded(user, currentDay, dailyCheckInReward);

        // Check if challenge is complete
        if (currentDay == 21 && !challenge.bonusClaimed) {
            challenge.isComplete = true;
            challenge.bonusClaimed = true;

            // Mint completion bonus
            veg21Token.mint(user, challengeCompletionBonus, "challenge_completion");
            totalRewardsEarned[user] += challengeCompletionBonus;
            totalRewardsDistributed += challengeCompletionBonus;
            totalChallengesCompleted++;

            emit ChallengeCompleted(user, challengeCompletionBonus);
        }
    }

    /**
     * @dev Returns challenge progress for a user
     * @param user Address of the user
     */
    function getChallengeProgress(address user) 
        external 
        view 
        returns (
            ChallengeType challengeType,
            uint256 startDate,
            uint256 checkInsCompleted,
            bool isComplete,
            uint256 daysRemaining
        ) 
    {
        Challenge storage challenge = userChallenges[user];

        uint256 remaining = 0;
        if (!challenge.isComplete && challenge.checkInsCompleted < 21) {
            remaining = 21 - challenge.checkInsCompleted;
        }

        return (
            challenge.challengeType,
            challenge.startDate,
            challenge.checkInsCompleted,
            challenge.isComplete,
            remaining
        );
    }

    /**
     * @dev Adds a new verifier
     * @param verifier Address to grant verifier role
     */
    function addVerifier(address verifier) external onlyOwner {
        require(verifier != address(0), "VEG21Rewards: verifier cannot be zero address");
        require(!isVerifier[verifier], "VEG21Rewards: already a verifier");

        isVerifier[verifier] = true;
        emit VerifierAdded(verifier);
    }

    /**
     * @dev Removes a verifier
     * @param verifier Address to revoke verifier role from
     */
    function removeVerifier(address verifier) external onlyOwner {
        require(isVerifier[verifier], "VEG21Rewards: not a verifier");

        isVerifier[verifier] = false;
        emit VerifierRemoved(verifier);
    }

    /**
     * @dev Updates reward amounts (owner only)
     * @param newDailyReward New daily check-in reward (in wei)
     * @param newCompletionBonus New completion bonus (in wei)
     */
    function setRewardAmounts(uint256 newDailyReward, uint256 newCompletionBonus) 
        external 
        onlyOwner 
    {
        require(newDailyReward > 0, "VEG21Rewards: daily reward must be positive");
        require(newCompletionBonus > 0, "VEG21Rewards: completion bonus must be positive");

        dailyCheckInReward = newDailyReward;
        challengeCompletionBonus = newCompletionBonus;

        emit RewardAmountsUpdated(newDailyReward, newCompletionBonus);
    }
}

contracts/VEG21Staking.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "./VEG21Token.sol";

/**
 * @title VEG21 Staking Contract
 * @notice Allows users to stake VEG21 tokens and earn rewards
 * @dev Implements simple staking with fixed APR and time-based rewards
 * 
 * Features:
 * - Stake VEG21 tokens to earn rewards
 * - Unstake tokens at any time
 * - Claim accumulated rewards
 * - Owner can adjust reward rate
 * - Emergency withdrawal functionality
 * 
 * Reward Mechanism:
 * - Rewards calculated based on time staked
 * - Default APR: 10% (adjustable by owner)
 * - Rewards distributed in VEG21 tokens
 */
contract VEG21Staking is Ownable, ReentrancyGuard {
    VEG21Token public immutable veg21Token;

    // Reward rate: 10% APR = 10000 basis points (adjustable)
    uint256 public rewardRateBasisPoints = 1000; // 10%
    uint256 public constant BASIS_POINTS = 10000;
    uint256 public constant SECONDS_PER_YEAR = 365 days;

    // Staking data
    struct StakeInfo {
        uint256 amount;
        uint256 startTime;
        uint256 lastClaimTime;
    }

    mapping(address => StakeInfo) public stakes;
    uint256 public totalStaked;

    // Events
    event Staked(address indexed user, uint256 amount);
    event Unstaked(address indexed user, uint256 amount);
    event RewardClaimed(address indexed user, uint256 reward);
    event RewardRateUpdated(uint256 oldRate, uint256 newRate);

    /**
     * @dev Constructor
     * @param _veg21Token Address of the VEG21 token contract
     * @param initialOwner Address of the initial owner
     */
    constructor(address _veg21Token, address initialOwner) Ownable(initialOwner) {
        require(_veg21Token != address(0), "VEG21Staking: token address cannot be zero");
        veg21Token = VEG21Token(_veg21Token);
    }

    /**
     * @dev Stakes VEG21 tokens
     * @param amount Amount of tokens to stake (in wei)
     */
    function stake(uint256 amount) external nonReentrant {
        require(amount > 0, "VEG21Staking: cannot stake 0 tokens");

        // If user already has a stake, claim pending rewards first
        if (stakes[msg.sender].amount > 0) {
            _claimRewards();
        }

        // Transfer tokens from user to contract
        require(
            veg21Token.transferFrom(msg.sender, address(this), amount),
            "VEG21Staking: transfer failed"
        );

        // Update stake info
        stakes[msg.sender].amount += amount;
        stakes[msg.sender].startTime = block.timestamp;
        stakes[msg.sender].lastClaimTime = block.timestamp;
        totalStaked += amount;

        emit Staked(msg.sender, amount);
    }

    /**
     * @dev Unstakes all staked tokens and claims rewards
     */
    function unstake() external nonReentrant {
        StakeInfo storage stakeInfo = stakes[msg.sender];
        require(stakeInfo.amount > 0, "VEG21Staking: no tokens staked");

        uint256 stakedAmount = stakeInfo.amount;

        // Claim rewards first
        _claimRewards();

        // Reset stake info
        stakeInfo.amount = 0;
        stakeInfo.startTime = 0;
        stakeInfo.lastClaimTime = 0;
        totalStaked -= stakedAmount;

        // Transfer staked tokens back to user
        require(
            veg21Token.transfer(msg.sender, stakedAmount),
            "VEG21Staking: transfer failed"
        );

        emit Unstaked(msg.sender, stakedAmount);
    }

    /**
     * @dev Claims accumulated staking rewards
     */
    function claimRewards() external nonReentrant {
        require(stakes[msg.sender].amount > 0, "VEG21Staking: no tokens staked");
        _claimRewards();
    }

    /**
     * @dev Internal function to claim rewards
     */
    function _claimRewards() private {
        uint256 reward = calculateReward(msg.sender);

        if (reward > 0) {
            stakes[msg.sender].lastClaimTime = block.timestamp;

            // Mint reward tokens to user
            veg21Token.mint(msg.sender, reward, "staking_reward");

            emit RewardClaimed(msg.sender, reward);
        }
    }

    /**
     * @dev Calculates pending rewards for a user
     * @param user Address of the user
     * @return Pending reward amount in wei
     */
    function calculateReward(address user) public view returns (uint256) {
        StakeInfo storage stakeInfo = stakes[user];

        if (stakeInfo.amount == 0) {
            return 0;
        }

        uint256 timeStaked = block.timestamp - stakeInfo.lastClaimTime;

        // Calculate reward: (staked amount * time staked * APR) / (seconds per year * basis points)
        uint256 reward = (stakeInfo.amount * timeStaked * rewardRateBasisPoints) 
            / (SECONDS_PER_YEAR * BASIS_POINTS);

        return reward;
    }

    /**
     * @dev Returns stake information for a user
     * @param user Address of the user
     * @return amount Amount staked
     * @return startTime Time when stake began
     * @return pendingReward Pending reward amount
     */
    function getStakeInfo(address user) 
        external 
        view 
        returns (uint256 amount, uint256 startTime, uint256 pendingReward) 
    {
        StakeInfo storage stakeInfo = stakes[user];
        return (
            stakeInfo.amount,
            stakeInfo.startTime,
            calculateReward(user)
        );
    }

    /**
     * @dev Updates the reward rate (owner only)
     * @param newRateBasisPoints New reward rate in basis points (e.g., 1000 = 10%)
     */
    function setRewardRate(uint256 newRateBasisPoints) external onlyOwner {
        require(newRateBasisPoints <= 5000, "VEG21Staking: rate cannot exceed 50%");

        uint256 oldRate = rewardRateBasisPoints;
        rewardRateBasisPoints = newRateBasisPoints;

        emit RewardRateUpdated(oldRate, newRateBasisPoints);
    }
}

contracts/VEG21Token.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Pausable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";

/**
 * @title VEG21 Token
 * @notice Official ERC20 token for the VEG21 vegan challenge platform
 * @dev Production-ready token contract with minting, burning, and access control
 * 
 * Features:
 * - Standard ERC20 functionality
 * - Role-based minting for rewards and staking contracts
 * - Burnable tokens for charity donations
 * - Pausable for emergency situations
 * - Owner-controlled access management
 * 
 * Token Economics:
 * - Name: VEG21 Token
 * - Symbol: VEG21
 * - Decimals: 18
 * - Initial Supply: Configurable at deployment
 * 
 * Use Cases:
 * 1. Challenge Rewards: Users earn VEG21 for completing 21-day vegan challenges
 * 2. Staking: Users stake VEG21 tokens to earn additional rewards
 * 3. Donations: Users donate VEG21 to supported vegan charities (tokens burned)
 * 4. P2P Transfers: Community token transfers within the VEG21 ecosystem
 * 
 * Deployment Networks:
 * - Celo Mainnet (Chain ID: 42220)
 * - Celo Alfajores Testnet (Chain ID: 44787)
 */
contract VEG21Token is ERC20, ERC20Burnable, ERC20Pausable, Ownable, AccessControl {
    // Role definitions
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    bytes32 public constant PAUSER_ROLE = keccak256("PAUSER_ROLE");

    // Events
    event TokensMinted(address indexed to, uint256 amount, string reason);
    event TokensBurned(address indexed from, uint256 amount, string reason);
    event MinterAdded(address indexed account);
    event MinterRemoved(address indexed account);

    /**
     * @dev Constructor initializes the token with name, symbol, and initial supply
     * @param initialSupply The initial token supply in whole tokens (will be converted to wei)
     * @param initialOwner The address that will receive the initial supply and owner rights
     */
    constructor(
        uint256 initialSupply,
        address initialOwner
    ) ERC20("VEG21 Token", "VEG21") Ownable(initialOwner) {
        require(initialOwner != address(0), "VEG21: owner cannot be zero address");

        // Grant roles to the initial owner
        _grantRole(DEFAULT_ADMIN_ROLE, initialOwner);
        _grantRole(MINTER_ROLE, initialOwner);
        _grantRole(PAUSER_ROLE, initialOwner);

        // Mint initial supply to owner
        if (initialSupply > 0) {
            _mint(initialOwner, initialSupply * 10**decimals());
            emit TokensMinted(initialOwner, initialSupply * 10**decimals(), "initial_supply");
        }
    }

    /**
     * @dev Mints new tokens to a specified address
     * @param to The address to mint tokens to
     * @param amount The amount to mint (in wei, not whole tokens)
     * @param reason Human-readable reason for minting
     * 
     * Requirements:
     * - Caller must have MINTER_ROLE
     * - Contract must not be paused
     * - Recipient cannot be zero address
     */
    function mint(address to, uint256 amount, string memory reason) 
        public 
        onlyRole(MINTER_ROLE) 
        whenNotPaused
    {
        require(to != address(0), "VEG21: mint to zero address");
        require(amount > 0, "VEG21: mint amount must be positive");

        _mint(to, amount);
        emit TokensMinted(to, amount, reason);
    }

    /**
     * @dev Burns tokens from a specified address
     * @param from The address to burn tokens from
     * @param amount The amount to burn (in wei)
     * @param reason Human-readable reason for burning
     * 
     * Requirements:
     * - Caller must be the token owner or have MINTER_ROLE
     * - Sender must have sufficient balance
     */
    function burnFrom(address from, uint256 amount, string memory reason) 
        public
    {
        require(
            from == _msgSender() || hasRole(MINTER_ROLE, _msgSender()),
            "VEG21: caller must be owner or minter"
        );
        require(balanceOf(from) >= amount, "VEG21: burn amount exceeds balance");

        _burn(from, amount);
        emit TokensBurned(from, amount, reason);
    }

    /**
     * @dev Pauses all token transfers
     * 
     * Requirements:
     * - Caller must have PAUSER_ROLE
     * 
     * Use Cases:
     * - Emergency situations
     * - Security incidents
     * - Contract upgrades
     */
    function pause() public onlyRole(PAUSER_ROLE) {
        _pause();
    }

    /**
     * @dev Unpauses all token transfers
     * 
     * Requirements:
     * - Caller must have PAUSER_ROLE
     */
    function unpause() public onlyRole(PAUSER_ROLE) {
        _unpause();
    }

    /**
     * @dev Adds a new minter address
     * @param account The address to grant MINTER_ROLE
     * 
     * Requirements:
     * - Caller must have DEFAULT_ADMIN_ROLE
     * 
     * Typical use: Authorize staking and rewards contracts
     */
    function addMinter(address account) public onlyRole(DEFAULT_ADMIN_ROLE) {
        require(account != address(0), "VEG21: minter cannot be zero address");
        grantRole(MINTER_ROLE, account);
        emit MinterAdded(account);
    }

    /**
     * @dev Removes a minter address
     * @param account The address to revoke MINTER_ROLE from
     * 
     * Requirements:
     * - Caller must have DEFAULT_ADMIN_ROLE
     */
    function removeMinter(address account) public onlyRole(DEFAULT_ADMIN_ROLE) {
        revokeRole(MINTER_ROLE, account);
        emit MinterRemoved(account);
    }

    /**
     * @dev Returns true if account has MINTER_ROLE
     */
    function isMinter(address account) public view returns (bool) {
        return hasRole(MINTER_ROLE, account);
    }

    /**
     * @dev Hook that is called before any transfer of tokens
     * Implements pausable functionality
     */
    function _update(
        address from,
        address to,
        uint256 value
    ) internal override(ERC20, ERC20Pausable) {
        super._update(from, to, value);
    }
}

contracts/VEG21TokenMock.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

/**
 * @title VEG21 Token Mock Contract
 * @notice This is a placeholder ERC20 token contract for the VEG21 dApp
 * @dev This contract will be deployed on Celo Alfajores testnet in Milestone 2
 * 
 * Features planned for Milestone 2:
 * - Standard ERC20 functionality (transfer, approve, transferFrom)
 * - Minting capabilities for challenge rewards
 * - Burning mechanism for donations
 * - Integration with VEG21 staking, donations, and rewards contracts
 * 
 * Network: Celo Alfajores Testnet (Chain ID: 44787)
 * RPC URL: https://alfajores-forno.celo-testnet.org
 * Explorer: https://alfajores.celoscan.io
 */

/**
 * @dev Interface of the ERC20 standard as defined in the EIP.
 */
interface IERC20 {
    /**
     * @dev Returns the total token supply.
     */
    function totalSupply() external view returns (uint256);

    /**
     * @dev Returns the account balance of another account with address `account`.
     */
    function balanceOf(address account) external view returns (uint256);

    /**
     * @dev Transfers `amount` tokens to address `recipient`.
     * Returns a boolean value indicating whether the operation succeeded.
     */
    function transfer(address recipient, uint256 amount) external returns (bool);

    /**
     * @dev Returns the remaining number of tokens that `spender` will be
     * allowed to spend on behalf of `owner` through {transferFrom}.
     */
    function allowance(address owner, address spender) external view returns (uint256);

    /**
     * @dev Sets `amount` as the allowance of `spender` over the caller's tokens.
     * Returns a boolean value indicating whether the operation succeeded.
     */
    function approve(address spender, uint256 amount) external returns (bool);

    /**
     * @dev Moves `amount` tokens from `sender` to `recipient` using the
     * allowance mechanism. `amount` is then deducted from the caller's allowance.
     * Returns a boolean value indicating whether the operation succeeded.
     */
    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) external returns (bool);

    /**
     * @dev Emitted when `value` tokens are moved from one account (`from`) to another (`to`).
     */
    event Transfer(address indexed from, address indexed to, uint256 value);

    /**
     * @dev Emitted when the allowance of a `spender` for an `owner` is set.
     */
    event Approval(address indexed owner, address indexed spender, uint256 value);
}

/**
 * @title VEG21Token
 * @dev Implementation of the VEG21 ERC20 Token
 * 
 * Token Economics:
 * - Name: VEG21 Token
 * - Symbol: VEG21
 * - Decimals: 18
 * - Initial Supply: 1,000,000 VEG21 (can be adjusted based on tokenomics)
 * 
 * Use Cases:
 * 1. Challenge Rewards: Users earn VEG21 for completing 21-day vegan challenges
 * 2. Staking: Users can stake VEG21 tokens to earn additional rewards
 * 3. Donations: Users can donate VEG21 to supported vegan charities
 * 4. Community Transfers: Peer-to-peer token transfers within the VEG21 ecosystem
 */
contract VEG21Token is IERC20 {
    // Token metadata
    string public name = "VEG21 Token";
    string public symbol = "VEG21";
    uint8 public decimals = 18;

    // Total supply tracking
    uint256 private _totalSupply;

    // Balance tracking
    mapping(address => uint256) private _balances;

    // Allowance tracking
    mapping(address => mapping(address => uint256)) private _allowances;

    // Contract owner (for minting privileges)
    address public owner;

    // Authorized minters (staking, rewards contracts)
    mapping(address => bool) public authorizedMinters;

    /**
     * @dev Events for administrative actions
     */
    event MinterAdded(address indexed minter);
    event MinterRemoved(address indexed minter);
    event TokensMinted(address indexed to, uint256 amount, string reason);
    event TokensBurned(address indexed from, uint256 amount, string reason);

    /**
     * @dev Modifier to restrict access to owner
     */
    modifier onlyOwner() {
        require(msg.sender == owner, "VEG21: caller is not the owner");
        _;
    }

    /**
     * @dev Modifier to restrict access to authorized minters
     */
    modifier onlyMinter() {
        require(authorizedMinters[msg.sender] || msg.sender == owner, "VEG21: caller is not authorized to mint");
        _;
    }

    /**
     * @dev Constructor - initializes the token with initial supply
     * @param initialSupply The initial token supply (in whole tokens, will be converted to wei)
     */
    constructor(uint256 initialSupply) {
        owner = msg.sender;
        authorizedMinters[msg.sender] = true;

        // Mint initial supply to contract deployer
        _totalSupply = initialSupply * 10**decimals;
        _balances[msg.sender] = _totalSupply;

        emit Transfer(address(0), msg.sender, _totalSupply);
    }

    /**
     * @dev Returns the total token supply
     */
    function totalSupply() public view override returns (uint256) {
        return _totalSupply;
    }

    /**
     * @dev Returns the balance of an account
     */
    function balanceOf(address account) public view override returns (uint256) {
        return _balances[account];
    }

    /**
     * @dev Transfers tokens to a recipient
     */
    function transfer(address recipient, uint256 amount) public override returns (bool) {
        _transfer(msg.sender, recipient, amount);
        return true;
    }

    /**
     * @dev Returns the remaining allowance
     */
    function allowance(address tokenOwner, address spender) public view override returns (uint256) {
        return _allowances[tokenOwner][spender];
    }

    /**
     * @dev Approves a spender to spend tokens
     */
    function approve(address spender, uint256 amount) public override returns (bool) {
        _approve(msg.sender, spender, amount);
        return true;
    }

    /**
     * @dev Transfers tokens from one account to another using allowance
     */
    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) public override returns (bool) {
        _transfer(sender, recipient, amount);

        uint256 currentAllowance = _allowances[sender][msg.sender];
        require(currentAllowance >= amount, "VEG21: transfer amount exceeds allowance");
        unchecked {
            _approve(sender, msg.sender, currentAllowance - amount);
        }

        return true;
    }

    /**
     * @dev Internal transfer function
     */
    function _transfer(
        address sender,
        address recipient,
        uint256 amount
    ) internal {
        require(sender != address(0), "VEG21: transfer from the zero address");
        require(recipient != address(0), "VEG21: transfer to the zero address");
        require(_balances[sender] >= amount, "VEG21: transfer amount exceeds balance");

        unchecked {
            _balances[sender] -= amount;
            _balances[recipient] += amount;
        }

        emit Transfer(sender, recipient, amount);
    }

    /**
     * @dev Internal approve function
     */
    function _approve(
        address tokenOwner,
        address spender,
        uint256 amount
    ) internal {
        require(tokenOwner != address(0), "VEG21: approve from the zero address");
        require(spender != address(0), "VEG21: approve to the zero address");

        _allowances[tokenOwner][spender] = amount;
        emit Approval(tokenOwner, spender, amount);
    }

    /**
     * @dev Mints new tokens (only authorized minters)
     * @param to The address to mint tokens to
     * @param amount The amount to mint
     * @param reason The reason for minting (e.g., "challenge_completion", "staking_reward")
     */
    function mint(address to, uint256 amount, string memory reason) public onlyMinter returns (bool) {
        require(to != address(0), "VEG21: mint to the zero address");

        _totalSupply += amount;
        _balances[to] += amount;

        emit Transfer(address(0), to, amount);
        emit TokensMinted(to, amount, reason);

        return true;
    }

    /**
     * @dev Burns tokens from an account
     * @param from The address to burn tokens from
     * @param amount The amount to burn
     * @param reason The reason for burning (e.g., "donation", "charity_contribution")
     */
    function burn(address from, uint256 amount, string memory reason) public returns (bool) {
        require(from == msg.sender || authorizedMinters[msg.sender], "VEG21: unauthorized burn");
        require(_balances[from] >= amount, "VEG21: burn amount exceeds balance");

        unchecked {
            _balances[from] -= amount;
            _totalSupply -= amount;
        }

        emit Transfer(from, address(0), amount);
        emit TokensBurned(from, amount, reason);

        return true;
    }

    /**
     * @dev Adds an authorized minter (only owner)
     */
    function addMinter(address minter) public onlyOwner {
        require(minter != address(0), "VEG21: minter is the zero address");
        authorizedMinters[minter] = true;
        emit MinterAdded(minter);
    }

    /**
     * @dev Removes an authorized minter (only owner)
     */
    function removeMinter(address minter) public onlyOwner {
        authorizedMinters[minter] = false;
        emit MinterRemoved(minter);
    }

    /**
     * @dev Transfers ownership of the contract (only owner)
     */
    function transferOwnership(address newOwner) public onlyOwner {
        require(newOwner != address(0), "VEG21: new owner is the zero address");
        owner = newOwner;
    }
}

/**
 * @title Deployment Notes for Milestone 2
 * 
 * Deployment Steps:
 * 1. Deploy VEG21Token contract with initial supply (e.g., 1,000,000 VEG21)
 * 2. Deploy VEG21Staking contract with VEG21Token address
 * 3. Deploy VEG21Donations contract with VEG21Token address
 * 4. Deploy VEG21Rewards contract with VEG21Token address
 * 5. Add staking and rewards contracts as authorized minters
 * 6. Transfer initial token allocation to staking rewards pool
 * 7. Update frontend config/contracts.ts with deployed addresses
 * 8. Test all contract interactions on Celo Alfajores
 * 9. Verify contracts on Celoscan
 * 
 * Required Environment Variables:
 * - VITE_VEG21_MODE=celo-testnet
 * - PRIVATE_KEY (for deployment)
 * - CELOSCAN_API_KEY (for verification)
 * 
 * Hardhat Configuration Example:
 * networks: {
 *   'celo-alfajores': {
 *     url: 'https://alfajores-forno.celo-testnet.org',
 *     accounts: [process.env.PRIVATE_KEY],
 *     chainId: 44787
 *   }
 * }
 */

